import React from "react";

interface Props {
  color: string;
  logoPreview: string | null;
  onColorChange: (e: React.ChangeEvent<HTMLInputElement>) => void;
  onLogoChange: (e: React.ChangeEvent<HTMLInputElement>) => void;
  onSave: () => void;
}

const BrandingSettings: React.FC<Props> = ({
  color,
  logoPreview,
  onColorChange,
  onLogoChange,
  onSave,
}) => {
  return (
    <div className="max-w-xl mx-auto bg-white p-6 rounded shadow space-y-6">
      <h2 className="text-2xl font-semibold">Theme Settings</h2>

      <div className="space-y-2">
        <label className="block font-medium">Primary Color</label>
        <input
          type="color"
          value={color}
          onChange={onColorChange}
          className="w-16 h-10 p-0 border-none cursor-pointer"
        />
      </div>

      <div className="space-y-2">
        <label className="block font-medium">Upload Logo</label>
        <input
          type="file"
          accept="image/*"
          onChange={onLogoChange}
          className="block"
        />
        {logoPreview && (
          <img src={logoPreview} alt="Preview Logo" className="h-16 mt-2" />
        )}
      </div>

      <button
        onClick={onSave}
        className="bg-blue-600 text-white px-6 py-2 rounded hover:bg-blue-700"
      >
        Save Theme
      </button>
    </div>
  );
};

export default BrandingSettings;
