import axios from "axios";

const API_URL = "http://localhost:8080/api/theme";

const getAuthToken = () => {
  return localStorage.getItem("token");
};

const apiClient = axios.create({
  baseURL: API_URL,
});

apiClient.interceptors.request.use((config) => {
  const token = getAuthToken();
  console.log(token);
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }
  return config;
});

export const getTheme = async () => {
  try {
    const response = await apiClient.get("/");
    return response.data;
  } catch (error) {
    if (axios.isAxiosError(error)) {
      throw new Error(error.response?.data || "Error fetching theme");
    } else {
      throw new Error("An unexpected error occurred");
    }
  }
};

export const saveTheme = async (theme: {
  primaryColor: string;
  logoUrl: string | null;
}) => {
  try {
    const response = await apiClient.post("/", theme);
    return response.data;
  } catch (error) {
    if (axios.isAxiosError(error)) {
      throw new Error(error.response?.data || "Error saving theme");
    } else {
      throw new Error("An unexpected error occurred");
    }
  }
};

export const uploadLogo = async (file: File) => {
  try {
    const formData = new FormData();
    formData.append("logo", file);

    const response = await apiClient.post("/logo", formData, {
      headers: {
        "Content-Type": "multipart/form-data",
      },
    });
    return response.data;
  } catch (error) {
    if (axios.isAxiosError(error)) {
      throw new Error(error.response?.data || "Error uploading logo");
    } else {
      throw new Error("An unexpected error occurred");
    }
  }
};
