import React, { useState } from "react";
import { useTheme } from "../context/ThemeContext";

const BrandingSettings: React.FC = () => {
  const { updateTheme } = useTheme();
  const [color, setColor] = useState("#1ABC9C");
  const [logoPreview, setLogoPreview] = useState<string | null>(null);

  const handleLogoUpload = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setLogoPreview(reader.result as string);
      };
      reader.readAsDataURL(file);
    }
  };

  const applyChanges = () => {
    updateTheme(color, logoPreview);
  };

  return (
    <div className="max-w-md mx-auto p-4 bg-white rounded shadow mt-6">
      <h2 className="text-lg font-semibold mb-2">Customize Branding</h2>

      <label className="block mb-2">Primary Color</label>
      <input
        type="color"
        value={color}
        onChange={(e) => setColor(e.target.value)}
        className="mb-4 w-full h-10"
      />

      <label className="block mb-2">Upload Logo</label>
      <input
        type="file"
        accept="image/*"
        onChange={handleLogoUpload}
        className="mb-4"
      />

      {logoPreview && (
        <div className="mb-4">
          <p>Preview:</p>
          <img src={logoPreview} alt="Preview" className="h-16" />
        </div>
      )}

      <button
        className="w-full bg-primary text-white py-2 rounded"
        onClick={applyChanges}
      >
        Apply Branding
      </button>
    </div>
  );
};

export default BrandingSettings;
