import React, { createContext, useContext, useState, useEffect } from "react";

interface ThemeContextType {
  primaryColor: string;
  logoUrl: string | null;
  updateTheme: (color: string, logo: string | null) => void;
}

const ThemeContext = createContext<ThemeContextType | null>(null);

export const ThemeProvider: React.FC = ({ children }) => {
  const [primaryColor, setPrimaryColor] = useState<string>(
    localStorage.getItem("primaryColor") || "#1ABC9C"
  );
  const [logoUrl, setLogoUrl] = useState<string | null>(
    localStorage.getItem("logoUrl")
  );

  const updateTheme = (color: string, logo: string | null) => {
    setPrimaryColor(color);
    setLogoUrl(logo);
    localStorage.setItem("primaryColor", color);
    if (logo) localStorage.setItem("logoUrl", logo);
  };

  useEffect(() => {
    document.documentElement.style.setProperty(
      "--tw-prose-links",
      primaryColor
    );
  }, [primaryColor]);

  return (
    <ThemeContext.Provider value={{ primaryColor, logoUrl, updateTheme }}>
      {children}
    </ThemeContext.Provider>
  );
};

export const useTheme = () => {
  const ctx = useContext(ThemeContext);
  if (!ctx) throw new Error("useTheme must be used within ThemeProvider");
  return ctx;
};
